using System;using System.Data;using DMZ.BL.Classes;using DMZ.Model.Model;using DMZ.UI.Basic;namespace DMZ.UI.UI.RH{    public partial class FrmSub : FrmClasse    {        private Sub _sub;        public FrmSub()        {            InitializeComponent();        }        private void FrmSub_Load(object sender, EventArgs e)        {            Campo1 = "Codigo";            Campo2 = "Descricao";            Ctabela = "sub";        }        public override void DefaultValues()        {            _sub = DoAddline<Sub>();            base.DefaultValues();        }        public override void Save()        {            FillEntity(_sub);            EF.Save(_sub);        }        public override void PreencheCampos(DataTable dt, int i)        {            _sub = FillControls(_sub, dt, i);        }    }}